import React from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { FBXLoader } from "three/examples/jsm/loaders/FBXLoader";
import { TextureLoader } from "three/src/loaders/TextureLoader";
import { useLoader } from "react-three-fiber";
import { useRef, useEffect } from "react";

const ChairMesh = ({ newMaterialOpt }) => {
  const theModel = useLoader(FBXLoader, "/wolf/wolf-model.fbx");
  const colorMap = useLoader(TextureLoader, "/wolf-material.png");
  const modelRef = useRef(theModel?.children[1]);

  useEffect(() => {
    if (theModel) {
      console.log("1", modelRef.current);
      console.log("2", colorMap);
      // theModel.material.map = colorMap;
    }
  }, [theModel]);

  return (
    // <primitive
    //   ref={modelRef}
    //   object={theModel}
    //   scale={[0.1, 0.1, 0.1]}
    //   rotation={[0, Math.PI, 0]}
    //   position={[0, -1, 0]}
    //   receiveShadow
    //   castShadow
    // ></primitive>
    <mesh ref={modelRef} geometry={theModel?.children[1].geometry}>
      <meshStandardMaterial map={colorMap} />
    </mesh>
  );
};

export default ChairMesh;
