import React from "react";
import * as THREE from "three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import { FBXLoader } from "three/examples/jsm/loaders/FBXLoader";
import { TextureLoader } from "three/src/loaders/TextureLoader";
import { useLoader } from "react-three-fiber";
import { useRef, useEffect } from "react";

const ChairMesh = ({ newMaterialOpt }) => {
  const theModel = useLoader(FBXLoader, "/wolf/wolf-model.fbx");
  const [colorMap,normalMap,roughnessMap, aoMap] = useLoader(TextureLoader, [
      "/wolf/color.png",
    // "/wolf/normal.png",
    // "/wolf/roughness.png",
    // "/wolf/metalic.png",
  ]);
  const modelRef = useRef(theModel?.children[0]);

  useEffect(() => {
    if (theModel) {
      console.log("1", theModel);
      console.log("11", theModel?.children[0].geometry);
      console.log("2", colorMap);
      // theModel.material.map = colorMap;
    }
  }, [theModel]);

  return (
    // <primitive
    //   ref={modelRef}
    //   object={theModel}
    //   scale={[0.1, 0.1, 0.1]}
    //   rotation={[0, Math.PI, 0]}
    //   position={[0, -1, 0]}
    //   receiveShadow
    //   castShadow
    // />
    <mesh ref={modelRef} geometry={theModel?.children[0].geometry}>
      <meshStandardMaterial
          map={colorMap}
          // normalMap={normalMap}
          // roughnessMap={roughnessMap}
          // aoMap={aoMap}
      />
    </mesh>
  );
};

export default ChairMesh;
